package org.example;

import org.example.assign
import org.junit.jupiter.api.Test
import org.junit.jupiter.api.Assertions.*

class LibraryTest {
    @Test
    fun changeValue() {
        val value = Value<Int>()
        assertNull(value.currentValue())
        value.changeValue(30)
        assertEquals(30, value.currentValue())
    }

    @Test
    fun operator() {
        var value = Value<Int>()
        assertNull(value.currentValue())
        value = 30
        assertEquals(30, value.currentValue())
    }
}
